const utils = require('./utils');

class Gateway {

    constructor ({sid, ip, port, token, password, iv}) {
        this.sid = sid;
        this.ip = ip;
        this.port = port;
        this.token = token;
        this.password = password;
        this.iv = iv;

        if (!sid) {
            throw new Error('[Gateway:constructor] Param error');
        }
    }

    update (data) {
        for (let key in data) {
            if (this.hasOwnProperty(key)) {
                this[key] = data[key];
            }
        }
    }
}

module.exports = Gateway;